INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('1','明日战绩','高分火爆热映中！古天乐亲任总监制！华语科幻年度震撼巨献！机甲大战外星生物，硬核守护家园！ 未来世界地球污染严重，一枚外太空陨石突如其来，带着外星生命体潘朵拉降临人间，袭击人类。空战部队泰来（古天乐饰）与队长郑重生（刘青云饰）前往执行拯救地球的任务，却遭遇前所未见的怪物袭击，机甲军团与外星异形生物展开超燃大战，千难万难誓死捍卫家园，上演了一场酣畅淋漓的激爽对决。然而，在牺牲队友身上意外发现的神秘信号，让泰来开始思考真相到底是什么，难道真正致命的并不是眼前的一切，而是自己身后迷雾之中一场巨大的阴谋…',99,'2022-08-05','https://p0.pipi.cn/mmdb/25bfd63302f0fa395b07accde068bfd3c361f.jpg?imageView2/1/w/464/h/644','动作 科幻 冒险');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('2','独行月球','人类为抵御小行星的撞击，拯救地球，在月球部署了月盾计划。陨石提前来袭，全员紧急撤离时，维修工独孤月（沈腾 饰）因为意外，错过了领队马蓝星（马丽 饰）的撤离通知，一个人落在了月球。不料月盾计划失败，独孤月成为了“宇宙最后的人类”，开始了他在月球上破罐子破摔的生活……',122,'2022-07-29','https://p0.pipi.cn/mmdb/25bfd6d72c992367cb537c020675f703a7045.jpg?imageView2/1/w/464/h/644','喜剧 科幻');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('3','猪猪侠大电影·海洋日记','超星特工猪猪侠在上次的任务中留下了后遗症，但他坚持独自潜入海底世界执行新任务。在五彩缤纷的海底世界见识到各种各样的海底生物，结识了正在寻找哥哥的小豆芽，受小豆芽勇敢执着的精神所打动，猪猪侠决定帮助小豆芽，帮他一起寻找哥哥。原来海洋世界发生了一件不为人知的大事，导致大量海洋“居民”失踪。这件事并不简单，背后隐藏着一个危及海洋世界和平的大危机。猪猪侠这次要如何克服困难，突破自我，解除海洋大危机呢？',85,'2022-08-06','https://p0.pipi.cn/mmdb/25bfd63371ff2a8ea32c956e191892497fdd4.jpg?imageView2/1/w/464/h/644','动画 冒险 喜剧');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('4','断·桥','黄雀市大桥突然垮塌，桥墩中一副被活埋多年的人骨赫然惊现。死者女儿闻晓雨（马思纯 饰）为追查父亲死因，得到一个自称孟超（王俊凯 饰）的男孩协助，并携手走上探寻真相与复仇之路。渐渐地，他们发现，闻晓雨的养父朱方正（范伟 饰）很可能就是杀人凶手，而他背后似乎隐藏着惊天的秘密…',113,'2022-08-13','https://p0.pipi.cn/mmdb/25bfd63351bdddf0eeaf33736324b208ccd73.jpg?imageView2/1/w/464/h/644','剧情 犯罪');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('5','神探大战','连环命案、私刑执法、预告杀人、以暴制暴...一群号称“神探”的团伙掀起了一场香港有史以来最邪恶的犯罪！患有精神疾病的“癫佬神探”李俊（刘青云 饰）为查真凶独闯犯罪现场！以“神探夫妇”陈仪（蔡卓妍 饰）和方礼信（林峯 饰）为首的重案组也开启了与杀戮赛跑的缉凶之路！敌友之间，正邪边缘，死亡审判的真相究竟是什么？真正的“神探”到底是谁？',101,'2022-07-08','https://p0.pipi.cn/mmdb/25bfd6d771f0fa57e267cb131f671a5889b2d.jpg?imageView2/1/w/464/h/644','犯罪 动作 悬疑');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('6','我和我的祖国','七位导演分别取材新中国成立70周年以来，祖国经历的无数个历史性经典瞬间。讲述普通人与国家之间息息相关密不可分的动人故事。聚焦大时代大事件下，小人物和国家之间，看似遥远实则密切的关联，唤醒全球华人共同回忆。',155,'2022-07-01','https://p0.pipi.cn/mmdb/d2dad59253780721f0cbae1df3d91a1758279.jpg?imageView2/1/w/464/h/644','剧情');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('7','精灵旅社3：疯狂假期','因为精灵旅社工作忙碌，德古拉和女儿梅维斯的关系逐渐疏远，德古谅纹设拉相信他注定永远孤独。梅维斯却把父亲的孤独误解为疲惫，于是把全家人都安排在豪华的怪物邮轮上，以此来放松自己，共度美好时光。德古拉的朋友科学怪人弗兰肯斯坦，狼人韦恩等人确信他会在邮轮上遇到某人后坠入情网。他刚斥责他们，这不是爱情之船，就有一个身穿白衣的美女艾丽卡令德古拉痴迷得近乎于痴呆。艾丽卡不但是个船长，她还是吸血鬼猎人范海辛的曾孙女 。',98,'2018-08-17','https://p0.pipi.cn/mmdb/d2dad5927e10fa7e120e133e8e384aca6880e.jpg?imageView2/1/w/464/h/644','喜剧 动画 奇幻');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('8','人间世','《人间世》是一部展现爱的本质的纪录电影。痛苦，疾病与死亡，是人生必然经历的三件事。然而，电影中的两所医院，两个家庭，在病痛中求索生命意义，学会沟通与成长。片中他们最终发现痛苦，疾病与死亡的正面价值，找寻到与其和解的有效方式——勇气、真诚与慈悲。',90,'2022-08-19','https://p0.pipi.cn/mmdb/25bfd63351b923ecd87a358864406665baccc.jpg?imageView2/1/w/464/h/644','纪录片');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('9','哥，你好','2022年金牌阵容喜剧，欢乐预定！与父亲老伍（魏翔 饰）终年不合的小伍（常远 饰）阴差阳错回到80年代，意外搅黄了父亲与母亲大陆（马丽 饰）的初次相遇。为了纠正这个错误，他想尽办法一次次重返过去，鼓励老妈追老爸，让人啼笑皆非的闹剧不断上演。父母相遇相恋的命运，是否会因为小伍的干预而转变？尘封在父亲心里的秘密，又将如何慢慢揭开……',112,'2022-08-03','https://p0.pipi.cn/mmdb/25bfd60206d51b51ba9235eb600dc98957e56.jpg?imageView2/1/w/464/h/644','喜剧 剧情');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('10','猎屠','府河市公安局接收到多起涉及全国以“恋爱”为名的“杀猪盘”电信诈骗案件，其中几起案件的受害者甚至因此自杀。通过多方侦查，警方所掌握到的犯罪线索悉数指向境外的一家公司——新盛集团。为保卫我国公民的生命财产安全，刑侦队长郑毅授命办理此案。由于案件涉及境外犯罪，侦破过程近乎孤立无援，原本锁定的屠夫接连离奇死亡，线索全断，郑毅等人在绝境中，步步为营、剥茧抽丝，背后的“大屠夫”逐渐原形毕露……',100,'2022-08-12','https://p0.pipi.cn/mmdb/25bfd63351bc7e537cd7c3127f613814099b1.jpg?imageView2/1/w/464/h/644','剧情 动作 犯罪');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('11','漫长的告白','少年时代的立冬曾爱慕过一位叫阿川的女子，但在某一天阿川的突然失踪成为了他十几年解不开的心结。为一解思念，立冬与哥哥立春去到与阿川同名的柳川，只为再见她一面。随着他们的再次相遇，很多过去的故事的真相浮出水面，立冬也知晓了阿川当年离开的真正原因，而他对阿川深藏的爱，却在这场远赴“她”乡的深情告白中愈演愈烈......',113,'2022-08-12','https://p0.pipi.cn/mmdb/25bfd6d77a3c693139d236a11319a3c514453.jpg?imageView2/1/w/464/h/644','剧情 爱情 家庭');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('12','人生大事','殡葬师莫三妹（朱一龙 饰）在刑满释放不久后的一次出殡中，遇到了孤儿武小文（杨恩又 饰），小文的出现，意外地改变了莫三妹对职业和生活的态度。',112,'2022-06-24','https://p0.pipi.cn/mmdb/25bfd671339c7e8ea33139d0476cb0d92908d.jpg?imageView2/1/w/464/h/644','剧情 家庭');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('13','隐入尘烟','两个被各自家庭抛弃掉的孤独个体，和庄稼一样沦为大地的孩子。在四季时光的流转中，他们那些散落在别人不解目光中的爱，都在比心洁净的土地里结出果实来……',133,'2022-07-08','https://p0.pipi.cn/mmdb/25bfd6712c9dddcbae7df52e61db7c152c22b.jpg?imageView2/1/w/464/h/644','剧情 爱情 现实 农村');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('14','七人乐队','七位导演自上世纪五十年代一直到当下，各自抽签负责一个年代，讲述普通人与香港之间息息相关的故事。七部短片分别是洪金宝的《练功》、许鞍华的《校长》、谭家明的《别夜》、袁和平的《回归》、杜琪峯的《遍地黄金》、林岭东的《迷路》，及徐克的《深度对话》。',111,'2022-07-29','https://p0.pipi.cn/mmdb/25bfd671c7e51bc696cf3e73c07f267bbe242.jpg?imageView2/1/w/464/h/644','剧情');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('15','外太空的莫扎特','任大望（黄渤 饰）一心想把喜好天文的儿子任小天（荣梓杉 饰）培养成钢琴演奏家，为此父子争吵不断。一天，神秘外星人莫扎特意外出现，从此，莫扎特帮助任小天开启了和爸爸“斗智斗勇”的生活。令任小天没有想到的是，莫扎特来到地球竟另有任务……',136,'2022-07-15','https://p0.pipi.cn/mmdb/25bfd6d7f2a8078ea3d7c304dc0f9d848c001.jpg?imageView2/1/w/464/h/644','奇幻 喜剧 家庭');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('16','盗梦空间','《盗梦空间》是由克里斯托弗·诺兰执导，莱昂纳多·迪卡普里奥，玛丽昂·歌迪亚等主演的电影。影片剧情游走于梦境与现实之间，被定义为“发生在意识结构内的当代动作科幻片”。影片讲述由莱昂纳多·迪卡普里奥扮演的造梦师，带领约瑟夫·高登-莱维特、艾伦·佩吉扮演的特工团队，进入他人梦境，从他人的潜意识中盗取机密，并重塑他人梦境的故事。该片于2020年8月12日起在全球重映。',148,'2022-07-30','https://bkimg.cdn.bcebos.com/pic/ac345982b2b7d0a26e5d3912cdef76094a369aac?x-bce-process=image/watermark,image_d2F0ZXIvYmFpa2UxODA=,g_7,xp_5,yp_5/format,f_auto','动作、悬疑、惊悚、科幻');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('17','蜘蛛侠3','蜘蛛侠（托比•马奎尔 Tobey Maguire 饰）在经历了重重考验之后，成为了人人敬仰的正义英雄，抱得美人归。但是，光环下蜘蛛侠，也有自己的心病：他在第一集时放走了小偷导致最亲爱的叔叔被小偷杀死，帕克一直耿耿于怀。',139,'2022-09-01','https://img1.doubanio.com/view/photo/l/public/p1344981868.webp','动作 科幻  冒险');
INSERT INTO public.movie (id,"name",introduction,duration,release_time,post_url,"types")
VALUES ('18','星际穿越','近未来的地球黄沙遍野，小麦、秋葵等基础农作物相继因枯萎病灭绝，人类不再像从前那样仰望星空，放纵想象力和灵感的迸发，而是每日在沙尘暴的肆虐下倒数着所剩不多的光景。',169,'2022-08-02','https://img1.doubanio.com/view/photo/l/public/p2614988097.webp','科幻');
